import React, { useState } from "react";
import {
  FaHome,
  FaUser,
  FaFolderOpen,
  FaEnvelope,
  FaMoon,
  FaSun,
  FaLinkedin,
  FaEnvelopeOpenText,
} from "react-icons/fa";
import Modal from "./Modal";

const Sidebar = ({ isOpen, closeSidebar, theme, toggleTheme }) => {
  const [contactModalOpen, setContactModalOpen] = useState(false);

  const openContactModal = () => {
    setContactModalOpen(true);
    closeSidebar(); // Close sidebar on mobile when modal opens
  };

  const closeContactModal = () => setContactModalOpen(false);

  return (
    <>
      <aside className={`sidebar ${isOpen ? "open" : ""}`}>
        <div
          className="sidebar-logo"
          onClick={closeSidebar}
          style={{ cursor: "pointer" }}
        >
          <FaFolderOpen
            style={{ color: "#bf00ff", width: 32, height: 32, marginRight: 14 }}
          />
          <span>My Portfolio</span>
        </div>
        <nav>
          <a href="#home" onClick={closeSidebar}>
            <FaHome
              style={{ color: "#bf00ff", width: 24, height: 24, marginRight: 14 }}
            />
            Home
          </a>
          <a href="#about" onClick={closeSidebar}>
            <FaUser
              style={{ color: "#bf00ff", width: 24, height: 24, marginRight: 14 }}
            />
            About
          </a>
          <a href="#projects" onClick={closeSidebar}>
            <FaFolderOpen
              style={{ color: "#bf00ff", width: 24, height: 24, marginRight: 14 }}
            />
            Projects
          </a>
          {/* Contact button opens modal */}
          <button
            onClick={openContactModal}
            className="contact-btn"
            style={{
              background: "none",
              border: "none",
              color: "var(--text-secondary)",
              fontWeight: "600",
              fontFamily: "'Montserrat', sans-serif",
              cursor: "pointer",
              display: "flex",
              alignItems: "center",
              padding: "0.75rem 1rem",
              borderRadius: "8px",
              width: "100%",
              textAlign: "left",
            }}
          >
            <FaEnvelope
              style={{ color: "#bf00ff", width: 24, height: 24, marginRight: 14 }}
            />
            Contact
          </button>
        </nav>

        {/* Dark/Light mode toggle */}
        <button
          className="theme-toggle-btn"
          onClick={toggleTheme}
          aria-label="Toggle dark mode"
        >
          {theme === "light" ? <FaMoon size={20} /> : <FaSun size={20} />}
          <span style={{ marginLeft: 8 }}>
            {theme === "light" ? "Dark Mode" : "Light Mode"}
          </span>
        </button>
      </aside>

      {/* Contact Modal */}
      <Modal isOpen={contactModalOpen} onClose={closeContactModal}>
        <h2>Contact Me</h2>
        <div className="contact-icons">
          <a
            href="https://www.linkedin.com/in/your-linkedin-username"
            target="_blank"
            rel="noopener noreferrer"
            aria-label="LinkedIn Profile"
          >
            <FaLinkedin />
          </a>
          <a href="mailto:your.email@example.com" aria-label="Send Email">
            <FaEnvelopeOpenText />
          </a>
        </div>
      </Modal>
    </>
  );
};

export default Sidebar;
