import React from "react";
import { FaHome, FaUser, FaFolderOpen, FaEnvelope, FaMoon, FaSun } from "react-icons/fa";

const Sidebar = ({ theme, toggleTheme }) => (
  <aside className="sidebar">
    <div className="sidebar-logo">
      <FaFolderOpen style={{ color: "#bf00ff", width: 32, height: 32, marginRight: 14 }} />
      <span>My Portfolio</span>
    </div>
    <nav>
      <a href="#home"><FaHome style={{ color: "#bf00ff", width: 24, height: 24, marginRight: 14 }} />Home</a>
      <a href="#about"><FaUser style={{ color: "#bf00ff", width: 24, height: 24, marginRight: 14 }} />About</a>
      <a href="#projects"><FaFolderOpen style={{ color: "#bf00ff", width: 24, height: 24, marginRight: 14 }} />Projects</a>
      <a href="#contact"><FaEnvelope style={{ color: "#bf00ff", width: 24, height: 24, marginRight: 14 }} />Contact</a>
    </nav>

    {/* Theme toggle button */}
    <button className="theme-toggle-btn" onClick={toggleTheme} aria-label="Toggle dark mode">
      {theme === "light" ? <FaMoon size={20} /> : <FaSun size={20} />}
      <span style={{ marginLeft: 8 }}>{theme === "light" ? "Dark Mode" : "Light Mode"}</span>
    </button>
  </aside>
);

export default Sidebar;
